using UnityEngine;using System.Collections;public class IdleModule : AIBaseModule {	public AnimationClip animationClip;	bool phasePlayed = false;	public override void Start ()	{		base.Start ();	}	public override void initialize_module ()	{		phasePlayed = false;	}	public override bool run_module ()	{        character.custom_look_at(character.target.transform.position);		if (phasePlayed == false) {			animation.Play(animationClip.name);			phasePlayed = true;		}		else {			if (!animation.IsPlaying(animationClip.name)) {				return false;			}		}		return true;	}}